INSERT INTO `portfolio` (`id`, `name`, `description`, `url`, `timestamp`, `imgurl`, `task`, `working`) VALUES
(1, 'Сайт-визитка {свадебного фотографа Ярослава Волкова}', '<p>Учитывая пожелания заказчика в дизайне и в функциональности сайта был нарисован минималистичный темный шаблон. В качестве карусели фотографий был использован АвтоВьювер.</p>\n<img src="/img/portfolio/yvwp1.jpg">\n<p>Часть сайта, отвечающая за администрирование позволяет редактировать количество и состав галерей, загружать в них фотографии и редактировать контактные данные.</p>\n<img src="/img/portfolio/yvwp2.jpg">\n<p>Этот проект реализован с использованием HTML5, CSS3, а так же чистого JavaScript, что позволило уменьшить размер и время загрузки сайта по сравнению с jQuery или другими фреймворками.</p>', 'www.yaroslavvolkov.com', '2015-10-12 13:32:48', 'yvwp.png', 'Сделать сайт-визитку с&nbsp;возможностью просмотра нескольких галерей в&nbsp;виде карусели фотографий и&nbsp;админкой.', 0),
(2, 'Доработка сайта {Анта-спорт}', '<p>Дизайн всплывающего окошка был предоставлен заказчиком, поэтому разработка сводилась к программированию и верстке.</p>\n<p>Сначала было сверстано окно, появляющееся спустя одну минуту после загрузки сайта. При переходе на другие страницы сайта таймер не сбрасывается. При нажатии на кнопку «Нет, спасибо» или по клику вне этого окна форма закрывается и пользователь может продолжить навигацию по сайту. При нажатии кнопки «Да» происходит переход к следующему окну.</p>\n<img src="/img/portfolio/anta-sport1.jpg">\n<p>Изначально планировалось сделать возможность выбрать удобное время для звонка.</p>\n<img src="/img/portfolio/anta-sport5.jpg">\n<p>В виде скрытого меню, появляющегося при клике по ссылке.</p>\n<img src="/img/portfolio/anta-sport4.jpg">\n<p>Позже заказчик решил убрать эту возможность, оставив только необходимые ему поля для осуществления обратного звонка.</p>\n<img src="/img/portfolio/anta-sport2.jpg">\n<p>Так же была добавлена кнопка в хедер, позволяющая открыть форму заказа обратного звонка в удобное пользователю время.</p>\n<img src="/img/portfolio/anta-sport3.jpg">\n<p>Данные из формы отправляются на указанные в настройках почтовые ящики, либо могут сохраняться в БД сайта.</p>\n<p>Этот проект реализован с использованием HTML5, CSS3, а так же чистого JavaScript, что позволило уменьшить размер и время загрузки сайта по сравнению с jQuery или другими фреймворками.</p>', 'www.anta-sport.ru', '2015-10-13 20:58:22', 'anta-sport.png', 'Сделать всплывающее через 1&nbsp;минуту окно с&nbsp;формой обратного звонка и&nbsp;добавить кнопку в&nbsp;заголовок.', 1),
(3, 'Разработка частей сайта {ФэмилиСпейс}', '<p>Для сайта была разработана админ-панель и раздел работы с архивными документами и книгами.</p>\n<img src="/img/portfolio/familyspace1.jpg">\n<img src="/img/portfolio/familyspace2.jpg">', 'www.familyspace.ru', '2015-10-11 14:06:00', 'familyspace.png', 'Сделать админ-панель и страницы с архивами книг.', 1),
(4, 'Переработка дизайна документации {сайта Платформали}', '<p>Заказчиком был предоставлен образец дизайна, в соответсвии с которым нужно было переработать существующее оформление документации.</p>\n<img src="/img/portfolio/platformaly3.png">\n<p>Текст был полностью переоформлен и переверстан. Особое внимание уделялось типографике.</p>\n<img src="/img/portfolio/platformaly4.png">\n<p>Так же изначально код в документации никак не подсвичавался.</p>\n<img src="/img/portfolio/platformaly1.png">\n<p>Была использована автоматическая подсветка кода, которая позволила улучшить читаемость кода.</p>\n<img src="/img/portfolio/platformaly2.png">\n<p>Этот проект реализован с использованием HTML5, CSS3.</p>', 'doc.platformaly.ru/sdk/', '2015-10-14 14:42:58', 'platformaly.png', 'Переработать дизайн документации', 0);

